mapper.insert:
insert into examnationItem (dId, eIName, elSpecification,
  eIFee, elFeeType, elCode
  )
values (#{dId,jdbcType=VARCHAR}, #{eIName,jdbcType=VARCHAR}, #{elSpecification,jdbcType=VARCHAR}, 
  #{eIFee,jdbcType=DOUBLE}, #{elFeeType,jdbcType=VARCHAR}, #{elCode,jdbcType=VARCHAR}
  )

mapper.select:
select eIId, dId, eIName, elSpecification, eIFee, elFeeType, elCode from examnationItem


    and eIId=#{eIId,jdbcType=INTEGER}


  and  dId = #{dId,jdbcType=VARCHAR}


   and  eIName = #{eIName,jdbcType=VARCHAR}


 and  elSpecification = #{elSpecification,jdbcType=VARCHAR}


  and  eIFee = #{eIFee,jdbcType=DOUBLE}


   and  elFeeType = #{elFeeType,jdbcType=VARCHAR}


   and  elCode = #{elCode,jdbcType=VARCHAR},
