mapper.select:
select mId, mCode, mName, mSpell, mSpecification, mUnit, mProducer, mType, mFee from medicine

  mName like "%"#{mName}"%"
  and mType like "%"#{mType}"%" 

   and mCode = #{mCode,jdbcType=VARCHAR}


  and  mSpell = #{mSpell,jdbcType=VARCHAR}


  and  mSpecification = #{mSpecification,jdbcType=VARCHAR}


   and mUnit = #{mUnit,jdbcType=VARCHAR}


  and  mProducer = #{mProducer,jdbcType=VARCHAR}


   and mFee = #{mFee,jdbcType=DOUBLE}
