mapper.select:
select registration.rId, registration.pId, pName, pBirth, pSex, pAddress from patient,registration

    pName like "%"#{pName}"%" and registration.pId=patient.pId and rStatus != '诊毕' and rStatus != '退号'
    and rId = #{rId}

mapper.update:
update registration
rStatus = #{rStatus}, rResult = #{rResult},
where rId=#{rId}
